(function(){let c=document.querySelector(".check-all");c.addEventListener("click",l);function l(){c.checked?document.querySelectorAll(".user-checkbox input").forEach(function(e){e.closest(".user-list").classList.add("selected"),e.checked=!0}):document.querySelectorAll(".user-checkbox input").forEach(function(e){e.closest(".user-list").classList.remove("selected"),e.checked=!1})}})();
